sys_variable_value.value:
(function execute(inputs, outputs) {

    const payload = JSON.parse(inputs.payload);
    outputs.trigger = {};
    outputs.trigger.case_type = payload.case_type;
    outputs.trigger.transaction_type = payload.transaction_type;

})(inputs, outputs);

