sys_variable_value.value:
(function execute(inputs, outputs) {
    try { 
        let payload = inputs.request_payload + "";
        if (gs.nil(payload)) {
            payload = "{}";
        }
        payload = JSON.parse(payload);
        const fields = inputs.required_fields.trim().split(",");
        const validator = new SCCInbound(payload).validatePayload(fields);
        outputs.has_error = validator.has_error;
        outputs.error_message = validator.error_message;
    } catch (error) {
        outputs.has_error = true;
        outputs.error_message = "Invalid JSON payload";
    }
})(inputs, outputs);

