sys_variable_value.value:
(function execute(inputs, outputs) {

    const taskRecord = inputs.task;
    
    const targetTask = new GlideRecord(taskRecord.getValue("sys_class_name"));
    targetTask.get(taskRecord.getUniqueValue());

    const sa = new GlideSysAttachment();
    outputs.attachment_id = sa.writeBase64(targetTask, inputs.file_name, inputs.content_type, inputs.data);

})(inputs, outputs);

