sys_variable_value.value:
(function execute(inputs, outputs) {

    outputs.has_error = false;
    outputs.error_message = "";
    outputs.scc_number = "";

    const statusCode = inputs.status_code + "";
    let body = "";

    try {
        body = JSON.parse(inputs.response_body);
    } catch (error) {
        // Handle invalid JSON, for 500 errors for example
        outputs.has_error = true;
        outputs.error_message = inputs.response_body;
        return;
    }

    if (statusCode === "200" || statusCode === "201") {
        outputs.scc_number = body.scc_number;
    } else {
        outputs.has_error = true;
        if (body.hasOwnProperty("error")) {
            outputs.error_message = body.error.message;
            if (body.error.hasOwnProperty("detail") && !gs.nil(body.error.detail)) {
                outputs.error_message += " - " + body.error.detail;
            }
        } else {
            outputs.error_message = body.response_message;
        }
    }

})(inputs, outputs);

