sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

	const html = gs.getMessage("failed_message_body", [
		"${URI_REF}",
		current.target_task.number + "",
		current.getValue("scc_id") || "",
		current.getValue("table").toUpperCase(),
		current.transaction_type.getDisplayValue().toUpperCase(),
		current.getValue("http_response_body") || "",
		"${mailto:mailto.reprocess_message}."
	]);

	template.print(html);

})(current, template, email, email_action, event);
